<template>    <div class="goods-select-popup">        <!-- 商品选择 -->        <g-select            v-model="selectData"            :type="type"            :maxNum="maxNum"            :disabled="disabled"            :extend="extend"        >            <slot name="btn"></slot>        </g-select>        <!-- 商品列表 -->        <g-table            v-if="extend.showTable"            v-model="selectData"            :disabled="disabled"            :extend="extend"        >        </g-table>    </div></template><script lang="ts" setup>import { useVModels } from "@vueuse/core";import {defineProps, withDefaults} from "vue";const GSelect = defineAsyncComponent(() => import('./select.vue'));const GTable = defineAsyncComponent(() => import('./table.vue'));const props = withDefaults(    defineProps<{        modelValue?: any        type?: string        disabled?: boolean        maxNum: number        extend?: any    }>(),    {        modelValue: [],        // 类型: 多选(multiple) ｜ 单选(single)        type: 'multiple',        // 是否禁用        disabled: false,        // 选择最大数量        maxNum: 10,        // 拓展extend        extend: {            name: '',            showTable: true        }    })const emit = defineEmits(['update:modelValue', 'update:goods_id', 'update:sku']);const { modelValue: selectData } = useVModels(props, emit);</script>