<template>    <!-- 表格 -->    <el-table        ref="tableRef"        size="large"        :data="selectData"        class="mt-4"        v-if="selectData?.length"    >        <el-table-column label="商品规格" min-width="240">            <template #default="{ row }">                <div class="flex items-center">                    <el-image                        fit="cover"                        :src="row.image"                        v-if="row.image"                        class="flex-none w-[58px] h-[58px]"                    />                    <div class="ml-4 overflow-hidden">                        <el-tooltip                            effect="dark"                            placement="top"                            :content="row?.name "                        >                            <div                                class="text-base line-2"                                style="line-height: 20px"                            >                                {{ row?.name }}                            </div>                        </el-tooltip>                        <div class="mt-[4px]">                            <el-tag checked v-if="row.specType == 2">多规格</el-tag>                        </div>                    </div>                </div>            </template>        </el-table-column>        <el-table-column label="商品价格" prop="price" min-width="180">            <template #default="{ row }">                <span v-if="row.specType == 2">                    ¥ {{ row.minPrice }} ~ {{ row.maxPrice }}                </span>                <span v-else>                    ¥{{ row.minPrice }}                </span>            </template>        </el-table-column>        <el-table-column            :label="extend.name+'价'"            min-width="140"        >            <template #default="{ row }">                <!--       多规格         -->                <g-spec-edit                    v-if="row.specType == 2"                    :model-value="row.skuList||row.prices"                    ref="specEdit"                    :disabled="disabled"                    :extend="extend"                >                    <el-button                        type="default"                        :plain="true"                    >                        设置秒杀价                    </el-button>                </g-spec-edit>                <!--       单规格         -->                <el-input                    v-else-if="row.specType == 1 && row.skuList"                    type="number"                    placeholder="请输入"                    :disabled="disabled"                    v-model="row.skuList[0][extend.priceType]"                />                <el-input                    v-else-if="row.specType == 1 && row.prices"                    type="number"                    placeholder="请输入"                    :disabled="disabled"                    v-model="row.prices[0][extend.priceType]"                />            </template>        </el-table-column>        <el-table-column            label="虚拟销量"            min-width="140"        >            <template #default="{ row }">                <el-input                    type="number"                    placeholder="请输入"                    :disabled="disabled"                    v-model="row.salesVolume"                />            </template>        </el-table-column>        <el-table-column            label="虚拟浏览量"            min-width="140"        >            <template #default="{ row }">                <el-input                    type="number"                    placeholder="请输入"                    :disabled="disabled"                    v-model="row.browseVolume"                />            </template>        </el-table-column>        <el-table-column            label="移除"            min-width="120"        >            <template #default="{ row, $index }">                <el-button                    type="primary"                    :link="true"                    :disabled="disabled"                    @click="removeItem($index)"                >                    移除                </el-button>            </template>        </el-table-column>    </el-table></template><script lang="ts" setup>import {useVModels} from '@vueuse/core';import {withDefaults} from "vue";const GSpecEdit = defineAsyncComponent(() => import('./spec-edit.vue'));const props = withDefaults(    defineProps<{        modelValue?: any        disabled: boolean        extend: any    }>(),    {        modelValue: [],        // 是否禁用        disabled: false,        // 拓展        extend: {}    })const emit = defineEmits(['update:modelValue']);const { modelValue: selectData } = useVModels(props, emit);const removeItem = (index: number) => {    selectData.value.splice(index, 1)}</script>