<template>    <!-- 表格 -->    <el-table        ref="tableRef"        size="large"        :data="selectData"        class="mt-4"        v-if="selectData?.length"    >        <el-table-column label="用户编号" prop="sn" min-width="100" />        <el-table-column label="用户昵称" min-width="240">            <template #default="{ row }">                <div class="flex items-center">                    <el-image                        fit="cover"                        :src="row.avatar"                        v-if="row.avatar"                        class="flex-none w-[58px] h-[58px]"                    />                    <div class="ml-4 overflow-hidden">                        <el-tooltip                            effect="dark"                            placement="top"                            :content="row?.nickname"                        >                            <div                                class="text-base line-2"                                style="line-height: 20px"                            >                                {{ row?.nickname }}                            </div>                        </el-tooltip>                    </div>                </div>            </template>        </el-table-column>        <el-table-column label="注册时间" prop="createTime" min-width="180" />        <el-table-column            label="移除"            min-width="120"        >            <template #default="{ row, $index }">                <el-button                    type="primary"                    :link="true"                    :disabled="disabled"                    @click="removeItem($index)"                >                    删除                </el-button>            </template>        </el-table-column>    </el-table></template><script lang="ts" setup>import {useVModels} from '@vueuse/core';import {withDefaults} from "vue";const props = withDefaults(    defineProps<{        modelValue?: any        disabled: boolean        extend: any    }>(),    {        modelValue: [],        // 是否禁用        disabled: false,        // 拓展        extend: {}    })const emit = defineEmits(['update:modelValue']);const { modelValue: selectData } = useVModels(props, emit);const removeItem = (index: number) => {    selectData.value.splice(index, 1)}</script>