<template>    <div class="coupon-list">        <div            class="coupon-card flex rounded-[5px] overflow-hidden h-[100px]"            v-for="item in couponList"            :key="item.id"        >            <!--    左侧    -->            <div                class="coupon-card__main w-[110px] text-white"            >                <div class="truncate">                    <span class="text-[17px]">¥</span>                    <span class="text-[30px]">{{ item.money }}</span>                </div>                <div class="text-sm px-[4px] line-2">                    {{ item.discountContent }}                </div>            </div>            <!--    右侧    -->            <div class="coupon-card__other flex-1">                <div class="flex flex-1 h-[70px] px-[10px]">                    <div class="flex flex-col justify-center flex-1 pr-[20px]">                        <!--  优惠券名称  -->                        <div class="text-[#101010] text-lg font-medium truncate w-[200px]">                            {{ item.name }}                        </div>                        <!--  使用日期  -->                        <div class="text-content text-xs mt-[5px]">                            {{ item.useTimeMsg }}                        </div>                    </div>                    <slot name="other"></slot>                </div>                <div                    class="text-xs text-content px-[10px] h-[30px] py-[5px]"                    style="border-top: 1px solid #EAEAEA"                >                    {{ item.useGoodsTypeMsg }}                </div>            </div>        </div>    </div></template><script setup>import { decorateCouponLists } from '@/api/decoration'const couponList = ref([])const getData = async () => {    const data = await decorateCouponLists()    couponList.value = data}getData()</script><style lang="scss" scoped>.coupon-card {  margin: 0 12px;  margin-top: 10px;  &__main {    display: flex;    justify-content: center;    flex-direction: column;    align-items: center;    background: linear-gradient(89.70deg, #f95f2f 0%, #ff2c3c 100%);  }  &__other {    background: #FFFFFF;  }}</style>