<template>    <el-card class="!border-none mt-4" shadow="never">        <template #header>            <span class="font-medium text-xl">用券规则</span>        </template>        <el-form-item label="用券时间" prop="useTimeType">            <div>                <div>                    <el-radio-group v-model="formData.useTimeType" :disabled="disabled">                        <el-radio :label="1">固定日期</el-radio>                        <el-radio :label="2">领券当日起</el-radio>                        <el-radio :label="3">领券次日起</el-radio>                    </el-radio-group>                </div>                <div class="mt-4">                    <daterange-picker                        v-if="formData.useTimeType == 1"                        style="width: auto"                        :disabled="disabled"                        v-model:startTime="formData.useTimeStart"                        v-model:endTime="formData.useTimeEnd"                    />                    <template v-if="formData.useTimeType != 1">                        <el-input                            class="w-[280px]"                            v-model.number="formData.useTime"                            placeholder="请输入"                            type="text"                            :disabled="disabled"                            :show-word-limit="true"                            :clearable="true"                        >                        </el-input>                        <span class="ml-2">天内可用</span>                    </template>                </div>            </div>        </el-form-item>    </el-card></template><script lang="ts" setup>import { useVModels } from '@vueuse/core'const props = withDefaults(    defineProps<{        modelValue?: any        disabled: boolean    }>(),    {        modelValue: {},        disabled: false    })const emit = defineEmits(['update:modelValue'])const { modelValue: formData } = useVModels(props, emit)</script>