<template>    <el-card class="!border-none mt-4" shadow="never">        <template #header>            <span class="font-medium text-xl">领取设置</span>        </template>        <el-form-item label="领取方式" prop="getType">            <div>                <el-radio-group v-model="formData.getType" :disabled="disabled">                    <el-radio :label="1">用户领取</el-radio>                    <el-radio :label="2">系统赠送</el-radio>                </el-radio-group>                <div class="form-tips">                    用户领取：指用户自行领取；系统赠送：指通过后台给某个用户发放                </div>            </div>        </el-form-item>        <el-form-item label="领券时间" prop="getTimeStart">            <div class="w-80">                <daterange-picker                    style="width: auto"                    type="daterange"                    format="YYYY-MM-DD"                    :disabled="disabled"                    v-model:startTime="formData.getTimeStart"                    v-model:endTime="formData.getTimeEnd"                />            </div>        </el-form-item>        <el-form-item label="领券次数" prop="getNumType">            <div>                <div>                    <el-radio-group v-model="formData.getNumType" :disabled="disabled">                        <el-radio :label="1">不限次数</el-radio>                        <el-radio :label="2">限制领取次数</el-radio>                        <el-radio :label="3">每天限制领取</el-radio>                    </el-radio-group>                </div>                <div class="mt-4" v-if="formData.getNumType != 1">                    <el-input                        class="w-[310px]"                        v-model.number="formData.getNum"                        placeholder="请输入"                        type="text"                        :disabled="disabled"                        :show-word-limit="true"                        :clearable="true"                    >                    </el-input>                    <span class="ml-2">次</span>                </div>            </div>        </el-form-item>    </el-card></template><script lang="ts" setup>import { useVModels } from '@vueuse/core'const props = withDefaults(    defineProps<{        modelValue?: any        disabled: boolean    }>(),    {        modelValue: {},        disabled: false    })const emit = defineEmits(['update:modelValue'])const { modelValue: formData } = useVModels(props, emit)</script>