<template>    <el-card class="!border-none mt-4" shadow="never">        <template #header>            <span class="font-medium text-xl">活动商品</span>        </template>        <el-form-item label="秒杀商品" prop="goods">            <goods-select                v-model="formData.goods"                max-num="25"                :extend="{                    name: '秒杀',                    showTable: true,                    showStatus: true,                    priceType: 'seckillPrice'                }"            >                <template #btn>                    <el-button                        type="primary"                    >                        选择商品                    </el-button>                    <span class="ml-4 text-[#999]">                        最多支持选择25件商品                    </span>                </template>            </goods-select>        </el-form-item>    </el-card></template><script lang="ts" setup>import { useVModels } from '@vueuse/core'const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: {}    })const emit = defineEmits(['update:modelValue'])const { modelValue: formData } = useVModels(props, emit)// 获取到秒杀商品参数watchEffect(() => {    const value = formData.value.goods    try {        const data = value.map((item: any) => {            return {                id: item.id,                image: item.image ?? '',                name: item.name ?? '',                specType: item.specType,                salesVolume: item.salesVolume ?? 0,                browseVolume: item.browseVolume ?? 0,                maxPrice: item.maxPrice,                minPrice: item.minPrice,                prices: item[item.skuList?'skuList':'prices'].map((sitem: any) => ({                    ...sitem,                    skuId: sitem.id,                    seckillPrice: sitem.seckillPrice                }))            }        })        formData.value.goods = data    }catch (error) {        console.log(error)    }})</script>