<template>    <el-card class="!border-none mt-4" shadow="never">        <template #header>            <span class="font-medium text-xl">活动规则</span>        </template>        <el-form-item label="每单限制" prop="limitStatus">            <div>                <div>                    <el-radio-group v-model="formData.limitStatus">                        <el-radio :label="0">不限制</el-radio>                    </el-radio-group>                </div>                <div>                    <el-radio-group v-model="formData.limitStatus">                        <el-radio :label="1">限制</el-radio>                    </el-radio-group>                    <el-input                        class="w-[200px] ml-4"                        v-model.number="formData.maxBuy"                        placeholder="请输入"                        type="text"                        :show-word-limit="true"                        :clearable="true"                    >                        <template #append>件</template>                    </el-input>                </div>                <div class="form-tips">                    每件商品单笔订单最多购买的件数。起购限制会影响每单限制，请合理设置                </div>            </div>        </el-form-item>    </el-card></template><script lang="ts" setup>import { useVModels } from '@vueuse/core'const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: {}    })const emit = defineEmits(['update:modelValue'])const { modelValue: formData } = useVModels(props, emit)</script>