<template>    <el-card class="!border-none mt-4" shadow="never">        <template #header>            <span class="font-medium text-xl">活动设置</span>        </template>        <el-form-item label="活动名称" prop="name">            <div class="w-80">                <el-input                    v-model="formData.name"                    placeholder="请输入活动名称"                    type="text"                    :show-word-limit="true"                    :clearable="true"                />            </div>        </el-form-item>        <el-form-item label="活动时间" prop="startTime">            <div class="w-80">                <daterange-picker                    style="width: auto"                    v-model:startTime="formData.startTime"                    v-model:endTime="formData.endTime"                />                <div class="form-tips">                    开始时间不得小于当前时间，结束时间不得小于开始时间                </div>            </div>        </el-form-item>        <el-form-item label="活动备注" prop="remarks">            <div class="w-80">                <el-input                    v-model="formData.remarks"                    placeholder="请输入活动备注"                    type="textarea"                    :autosize="{ minRows: 4, maxRows: 4 }"                    maxlength="64"                    :show-word-limit="true"                    :clearable="true"                />            </div>        </el-form-item>    </el-card></template><script lang="ts" setup>import { useVModels } from '@vueuse/core'const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: {}    })const emit = defineEmits(['update:modelValue'])const { modelValue: formData } = useVModels(props, emit)</script>