<template>    <view class="checkbox" @click="toggleChecked">        <!--    选中    -->        <text            v-show="isChecked"            class="iconfont icon-xuanzhong"            :style="{                'color': activeColor,                'font-size': addUnit(iconSize)            }"        ></text>        <!--    未选中    -->        <text            v-show="!isChecked"            class="iconfont icon-weixuan"            :style="{                'color': inactiveColor,                'font-size': addUnit(iconSize)            }"        ></text>        <text class="ml-[10rpx]">{{ label }}</text>    </view></template><script lang="ts" setup>/** * @description 单个多选狂 * @property {string} label 标签 * @property {string|number} iconSize 图标大小 * @property {any} modelValue 是否选中的值 * @property {string} activeColor 选中的颜色 * @property {string} inactiveColor 未选中的颜色 * @property {string | number | boolean} trueLabel 选中时的值 * @property {string | number | boolean} defaultChecked 没有选中时的值 * @example <l-checkbox v-model="" label=“超级大八鸡” :true-label="1" :false-label="0" /> */import {ref, watch, watchEffect} from "vue";import { addUnit } from "@/utils/util";const emit = defineEmits(['change', 'update:modelValue'])const props = withDefaults(defineProps<{    label?: string    iconSize?: string | number    checked: string | number | boolean    modelValue?: any    activeColor?: string    inactiveColor?: string    defaultChecked?: string | number | boolean    trueLabel?: string | number | boolean    falseLabel?: string | number | boolean}>(), {    label: '',    iconSize: '34',    checked: 'default',    modelValue: 'default',    activeColor: '#007aff',    inactiveColor: '#c5c5c5',    defaultChecked: false,    trueLabel: true,    falseLabel: false})const isChecked = ref(props.defaultChecked)watchEffect(() => {    const value = props.modelValue    const checked = props.checked    if (props.checked != 'default') isChecked.value = checked    if (props.modelValue != 'default') isChecked.value = value})const toggleChecked = () => {    isChecked.value = !isChecked.value    if (isChecked.value) {        isChecked.value = props.falseLabel    } else {        isChecked.value = props.trueLabel    }    emit('change', isChecked.value)    emit('update:modelValue', isChecked.value)}</script><style lang="scss" scoped>@import './iconfont/iconfont.css';.checkbox {    display: flex;    align-items: center;}</style>